import React, { Component } from 'react';
import { BrowserRouter, Switch, Route, Redirect } from 'react-router-dom'
import logo from './logo.svg';
import './Signup.css';

class Signup extends Component {
  constructor(props) {
    super(props);

    this.state = { email: '',  username: '', password: '', confPassword: ''};

    this.handleSubmit = this.handleSubmit.bind(this);
    this.onChange = this.onChange.bind(this);
  }

  onChange(e) {
    //console.log(e.target.value)
       this.setState({
           [e.target.name]: e.target.value
       });
   }

  handleSubmit(event){
    event.preventDefault()
    console.log("uhm?");
    console.log(this.state.password)

      fetch("http://localhost:5000/signup", {
            mode: 'cors',
            method: 'POST',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              email: this.state.email, //Your roomname,
              username: this.state.username, //Your nickname
              password: this.state.password, //message content
              confPassword: this.state.confPassword,

              // more data if you want
            })
          })
          .then(res => res.text())
          .then(data => {
            console.log(data);
            //return res;
            // process returned data
          }).catch(err => {
            // handle err
            console.log(err)
          })
  }

  render() {

    return (
      <div className = "container">

      <form onSubmit={this.handleSubmit}>
        <div className = "username-signup">
          <p> Email address </p>
          <input type="text" id="input-signup" name="email" onChange={this.onChange} value={this.state.email}/>
        </div>

        <div className = "username-signup">
          <p> Account name</p>
          <input type="text" id="input-signup" name="username" onChange={this.onChange} value={this.state.username}/>
        </div>

        <div className = "username-signup">
          <p> Phone number </p>
          <input type="text" id="input-signup"/>
        </div>

        <div className = "password-signup">
          <p> Password </p>
          <input type="password" id = "input-signup" name="password" onChange={this.onChange} value={this.state.password}/>
        </div>


        <div className = "password-signup">
          <p> Reconfirm password </p>
          <input type="password" id = "input-signup" name="confPassword" onChange={this.onChange} value={this.state.confPassword}/>
        </div>


        <input type="submit" value="Sign up" id ="submitButton-signup"/>
      </form>

      </div>
    );
  }
}

export default Signup;
