import React, { Component } from 'react';
import './App.css';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';


class Medicine extends Component {
    constructor (props){
        super(props)

        this.state = {
            color: '',
            open: false,
            textFieldValue: '',
            cancel: '',
            showReward: false
        }

        this.changeColor = this.changeColor.bind(this);
        this.handleClickOpen = this.handleClickOpen.bind(this);
        this.handleOK = this.handleOK.bind(this);
        this.handleCancel = this.handleCancel.bind(this);
        this.handleTextFieldChange = this.handleTextFieldChange.bind(this);
        this.handleOKReward = this.handleOKReward.bind(this);
        this.handleCancelReward = this.handleCancelReward.bind(this);
    }

    handleTextFieldChange(e) {
      this.setState({
          textFieldValue: e.target.value
      });
    }

    changeColor(){
        // CATH: change this.state.color === "#6ADC83"
      // if (this.state.color === '#4AB3F4'){
      //   return;
      // }

        if (this.state.color === '#6ADC83'){
            return;
        }

      fetch("http://localhost:5000/verifyPassword", {
          mode: 'cors',
          credentials: 'include',
          method: 'POST',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
            'Access-Control-Allow-Credentials': true,
          },
            body: JSON.stringify({
              password: this.state.textFieldValue,
            })
          })
          .then(res => res.text())
          .then(data => {
            console.log(data)
            if (data === "OK"){
              fetch("http://localhost:5000/addDate", {
                  mode: 'cors',
                  credentials: 'include',
                  method: 'POST',
                  headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Credentials': true,
                  },
                    body: JSON.stringify({
                      medicine: this.props.med,
                    })
                  })
                  .then(res => res.text())
                  .then(data => {
                    if (data === "equal"){
                      fetch("http://localhost:5000/incrementReward", {
                          mode: 'cors',
                          credentials: 'include',
                          method: 'POST',
                          headers: {
                            'Accept': 'application/json',
                            'Content-Type': 'application/json',
                            'Access-Control-Allow-Credentials': true,
                          },
                          })
                          .then(res => res.text())
                          .then(data => {
                            if (data === "goal"){
                              // make a post to set all the checked for today to false??
                              this.setState({showReward: true})
                              this.props.rerenderParentCallback();
                              console.log("The reward should be deleted.");
                            }

                          });
                    }
                  });
                  // change the checked to true ^


              // is totla num medicatoin = num blue circs??
                // then tell to increment rewards.

              var newColor;
              //CATH: change newColor to #6ADC83 (green)
              //   newColor = '#4AB3F4';
              newColor = '#6ADC83';
              this.setState({color: newColor});
              this.setState({ open: false });
            }
          }).catch(err => {
            // handle err
            console.log(err)
          })

    }

    componentDidMount(){
      var today = new Date();
      var dd = String(today.getDate()).padStart(2, '0');
      var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
      var yyyy = today.getFullYear();

      today = mm + '/' + dd + '/' + yyyy;

      fetch("http://localhost:5000/getDate?name=" + this.props.med, {
          mode: 'cors',
          credentials: 'include',
          method: 'GET',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
            'Access-Control-Allow-Credentials': true,
          },
      })
      .then(res => res.json())
      .then(data => {

        var dates = []

        if (typeof data[0] != 'undefined'){
          for (var x in data[0].dates){
            var d = new Date(data[0].dates[x]).getTime()
            dates.push(d)
          }
        }

        // console.log(data[0])
        //  var d = new Date(data[0].dates[0])
        //  console.log(d)
        // // uhh this has to be changed too
        //
        // //console.log(data[0].dates)
        // var dates = data[0].dates

        // cut all of the dates stuff off
        today = new Date()
        today.setHours(0, 0, 0, 0)

        //console.log(dates)
        if (dates.includes(today.getTime())){
          //  CATH: change color: to color: #6ADC83
          //return this.setState({color: '#4AB3F4'})
            return this.setState({color: '#6ADC83'})
        }
        else {
          // make a post to set to false!
          return this.setState({color: 'white'})
        }

      });
    }

    handleClickOpen = () => {
  this.setState({ open: true });
};

  handleCancel = () => {
    this.setState({ open: false });
    this.setState({ cancel: true });
  };

  handleCancelReward = () => {
    this.setState({ showReward: false });
  };

  handleOK = () => {
    this.changeColor();


    this.setState({ cancel: false });

  };

  handleOKReward = () => {
    this.setState({ showReward: false });
  };


  render(){
    return (

      <div className = "one-medicine">

      <Dialog
        open={this.state.showReward}
        onClose={this.handleCancelReward}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Reward achieved!"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Congrats!
          </DialogContentText>
            You've reached your goal count!
        </DialogContent>
        <DialogActions>
          <Button onClick={this.handleOKReward} color="primary" autoFocus>
            OK
          </Button>
        </DialogActions>
      </Dialog>

      <Dialog
        open={this.state.open}
        onClose={this.handleCancel}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Please enter a password."}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            To check off your medication a password is required.
          </DialogContentText>
          <TextField
              autoFocus
              margin="dense"
              // id="name"
              label="Password"
              value={this.state.textFieldValue} onChange={this.handleTextFieldChange}
              type="password"
              fullWidth
            />
        </DialogContent>
        <DialogActions>
          <Button onClick={this.handleCancel} color="primary">
            Cancel
          </Button>
          <Button onClick={this.handleOK} color="primary" autoFocus>
            OK
          </Button>
        </DialogActions>
      </Dialog>

        <p className="medicine-title"> {this.props.med}</p>
        <div className = "circly" onClick ={this.handleClickOpen} style ={{background: this.state.color}}> </div>
      </div>
    );
  }
}

export default Medicine;
