import React, { Component } from 'react';
import { Redirect } from 'react-router-dom'
import './App.css';
import Button from '@material-ui/core/Button';
import Medicine from './Medicine';
import Calendar from 'react-calendar';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import TextField from '@material-ui/core/TextField';
import Checkbox from '@material-ui/core/Checkbox';
import iconprofile from "./images/iconprofile2.png";
import footerHome from "./images/web-page-home.png";
import footerProfile from "./images/smile.png"
// import moment from "moment";
import moment from "moment-timezone";
import { BarLoader } from 'react-spinners';

class Home extends Component {
  constructor(props) {

    super(props);

    this.state = {
      medicine: '',
      medicine_array: [],
      reward_array: [],
      image: '',
      avatar:'',
      open: false,
      medDesc: '',
      Sunday: false,
      completed: null,
      username:'',
      medicine_render: false,
      scroll: ''
    }

    this.handleCancel = this.handleCancel.bind(this);
    this.handleOK = this.handleOK.bind(this);
    this.handleClickOpen = this.handleClickOpen.bind(this);

    this.renderReward = this.renderReward.bind(this);
    this.renderMedicine = this.renderMedicine.bind(this);

    this.onChange = this.onChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.getMedList = this.getMedList.bind(this);
    this.goProfile = this.goProfile.bind(this);
    this.getImage = this.getImage.bind(this);
    this.getAvatar = this.getAvatar.bind(this);
    this.routeChange = this.routeChange.bind(this);

    this.onCheck = this.onCheck.bind(this);
    this.rerenderParentCallback = this.rerenderParentCallback.bind(this);
    this.rerenderParentCallbackMedicine = this.rerenderParentCallbackMedicine.bind(this);

    //CATH
    this.handleScroll = this.handleScroll.bind(this);

    this.IMAGES = {
      icecream: require('./images/rewards/icecream.png'), // statically analyzed
      baking: require('./images/rewards/baking.png'),
      ball: require('./images/rewards/ball.png'),
      beach: require('./images/rewards/beach.png'),
      bike: require('./images/rewards/bike.png'),
      book: require('./images/rewards/book.png'),
      bowling: require('./images/rewards/bowling.png'),
      card: require('./images/rewards/card.png'),
      carnival: require('./images/rewards/carnival.png'),
      chess: require('./images/rewards/chess.png'), //10
      clothes: require('./images/rewards/clothes.png'),
      ferris: require('./images/rewards/ferris.png'),
      fire: require('./images/rewards/fire.png'),
      game: require('./images/rewards/game.png'),
      gift: require('./images/rewards/gift.png'),
      hamburger: require('./images/rewards/hamburger.png'),
      hotdog: require('./images/rewards/hotdog.png'),
      jewelry: require('./images/rewards/jewelry.png'),
      kart: require('./images/rewards/kart.png'),
      makeup: require('./images/rewards/makeup.png'), //10
      movies: require('./images/rewards/movies.png'),
      paint: require('./images/rewards/paint.png'),
      picnic: require('./images/rewards/picnic.png'),
      pizza: require('./images/rewards/pizza.png'),
      puzzle: require('./images/rewards/puzzle.png'),
      shopping: require('./images/rewards/shopping.png'),
      sneakers: require('./images/rewards/sneakers.png'),
      swimming: require('./images/rewards/swimming.png'),
      teddy: require('./images/rewards/teddy.png'),
      tennis: require('./images/rewards/tennis.png')
    }

    this.HEADS = {
      bunny: require('./images/heads/bunny.png'), // statically analyzed
      cat: require('./images/heads/cat.png'),
      dinosaur: require('./images/heads/dinosaur.png'),
      dog: require('./images/heads/dog.png'),
      frog: require('./images/heads/frog.png'),
      monkey: require('./images/heads/monkey.png'),
      panda: require('./images/heads/panda.png'),
      penguin: require('./images/heads/penguin.png'),
      raccoon: require('./images/heads/raccoon.png'),
      unicorn: require('./images/heads/unicorn.png'), //10
    }

    this.server = "http://localhost:5000";

    // //cath
    // // this.location = this.props.location.pathname;
    // console.log(window.location.pathname);
  }

  rerenderParentCallback(){
    this.setState({reward_array: []});
  }

  rerenderParentCallbackMedicine(){
    this.setState({medicine_render: true});
  }

  getImage(name) { // dynamically invoked
    return this.IMAGES[name];
  }

  getAvatar(name) { // dynamically invoked
    return this.HEADS[name];
  }

  onChange(e) {
       this.setState({
           [e.target.name]: e.target.value
       });
   }

   onCheck(e) {
     this.setState({
         [e.target.name]: e.target.checked
     });
   }

  routeChange(event) {
    //this.setState({toProfile: true});
    this.props.history.push("/profile");
    // event.preventDefault()
    // console.log("succesfully here!?");
    //
    // fetch("http://localhost:5000/check", {
    //   mode: 'cors',
    //   credentials: 'include',
    //   method: 'POST',
    //   headers: {
    //     'Accept': 'application/json',
    //     'Content-Type': 'application/json',
    //     'Access-Control-Allow-Credentials': true,
    //   }
    // }).catch(err => {
    //   // handle err
    //   console.log("this bitch did not work")
    // })
  }


  componentDidMount() {
    console.log("parent mounting")
    var today = new Date()
    today.setHours(0, 0, 0, 0)
    today = moment(today).tz("America/New_York").format("YYYY/MM/DD");

    //cath
    if (window.location.pathname == "/home") {
      console.log("ayayaya");
    }

    fetch(this.server + "/getMedicine?date=" + today, {
      mode: 'cors',
      credentials: 'include',
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Credentials': true,
      },
    })
    .then(res => res.json())
    .then(data => {
      var thing = []
      for (var x in data){
        thing.push(data[x].medicine);
      }

      this.setState({medicine_array: thing})
    });

    fetch(this.server + "/getReward", {
      mode: 'cors',
      credentials: 'include',
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Credentials': true,
      }
    })
    .then(res => res.json())
    .then(data => {
      //console.log(data[0])
      //console.log(data[0])
      if (typeof data[0] != 'undefined'){
          this.setState({reward_array: data[0], image: data[0].img_path })
      }

    });

    fetch(this.server + "/getAvatar", {
      mode: 'cors',
      credentials: 'include',
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Credentials': true,
      }
    })
    .then(res => res.json())
    .then(data => {
      console.log("USER DATA", data)
      // console.log("Username?", data.username)

      if (typeof data != 'undefined' && data !== null){
          this.setState({username: data.username, avatar: data.avatar_path});
      }
    });


    fetch(this.server + "/getCompleted", {
      mode: 'cors',
      credentials: 'include',
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Credentials': true,
      }
    })
    .then(res => res.json())
    .then(data => {
      console.log(data)

      if (data.length !== 0){
        // var completed_dates = []
        // for (var i in data){
        //   completed_dates.push(data[i].completed_dates);
        // }

        this.setState({completed: data[0].completed_dates })
      }
      else {
        this.setState({completed: []})
      }
    });
      // console.log(completed_dates)


  }



  handleSubmit(event){
    event.preventDefault()
    var today = new Date()
    today.setHours(0, 0, 0, 0)
    today = moment(today).tz("America/New_York").format("YYYY/MM/DD");

      fetch(this.server + "/addMedicine", {
          mode: 'cors',
          credentials: 'include',
          method: 'POST',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
            'Access-Control-Allow-Credentials': true,
          },
            body: JSON.stringify({
              medicine: this.state.medicine
            })
          })
          .then(res => res.text())
          .then(data => {
            // console.log("hiiii")
            // this.setState({form: false})

            fetch(this.server + "/getMedicine?date=" + today, {
              mode: 'cors',
              credentials: 'include',
              method: 'GET',
              headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Access-Control-Allow-Credentials': true,
              },
            })
            .then(res => res.json())
            .then(data => {
              var thing = []
              for (var x in data){
                thing.push(data[x].medicine);
              }

              this.setState({medicine_array: thing})
            });
            // if (data.data.code === 200){
            //
            // }
            // console.log(data);
            //return res;
            // process returned data
          }).catch(err => {
            // handle err
            console.log(err)
          })
  }


  // showForm(){
  //   if (this.state.form === true ){
  //     return (
  //       <div className = "display-form">
  //         <form onSubmit={this.handleSubmit}>
  //
  //         <input type="text" id ="medicine-add" name="medicine" onChange={this.onChange} value={this.state.medicine}/>
  //
  //         <input type="submit" value="Add" id ="submitButton-home" onClick={this.removeForm}/>
  //         </form>
  //       </div>
  //     );
  //   }
  // }
  //
  // setForm(){
  //   this.setState({form: true});
  // }

  getMedList(){

    return (this.state.medicine_array.map((data, index) => {
      return (<Medicine key= {index} med={data} rerenderParentCallback={this.rerenderParentCallback}
        rerenderParentCallbackMedicine={this.rerenderParentCallbackMedicine}/>) })
    );


    // return (this.state.medicine_array.map( ( med ) => {
    //   console.log(med)
    //     return (<Medicine medicine={med} />) ;
    //   });
    // )
}

renderUser(){
  console.log("THIS IS THE AVATAR", this.state.avatar)

  if (this.state.avatar !== "" && typeof this.state.avatar !== 'undefined'){
    var thumbnail = this.getAvatar(this.state.avatar);

    return ( <img src={thumbnail} alt="thumbnail" width="120" height="120"/> );
  }
  else{
    return (<div id = "circle"> </div>);
  }
}

//
// <Button id = "editbutton" variant="outlined" onClick ={this.handleClickOpen}>
//   + Add medications
// </Button>

  /* Render the medicines if they exist. AKA, get request here... otherwise,
   * just render the edit button
  */
  renderMedicine(){
    if (this.state.medicine_array.length === 0){
      return (
        <div className="med-container">

        <div className="deargod-top" id="medicationNoneTop">
          {/*<div className="pointer"></div>*/}
          <p className="deargodTopTitle" id="med">
            My Medication
          </p>
        </div>
        <div className = "deargod-med" id="medicationNone">



          {/*<div className="deargod-bottom">*/}
            <p id="notif"> You don't have any medications for today.
              {/*<a href="/profile"><span className="signup-here">Add one here!</span></a> */}
            </p>
          {/*</div>*/}
        </div>
        </div>
      );
    }
    else {
      return (
          <div className="med-container">

            <div className = "deargod-med">
              <div className="deargod-top" id="medicationNoneTop">
              {/*<div className="pointer"></div>*/}
                <p className="deargodTopTitle" id="med">
                My Medication
                </p>
              </div>

              <div className="med-displayed-innner">
                {/*add the deargod-top*/}
                {this.getMedList()}
              </div>
            </div>
          </div>
      );
    }
  }

  goProfile(){
    this.props.history.push("/reward");
  }

  // oh fuck bugs
  // cannot render img and also wont render at beginning
  renderReward(){
    // console.log( this.state.reward_array.desc )
    // console.log( this.state.reward_array.length )
    // console.log(this.state.reward_array.desc == undefined)
    //|| typeof this.state.reward_array.img_path === 'undefined'

    if (this.state.reward_array.length === 0 ){
      //console.log("hi")

      return (
          <div className="rewards-container">
            <div className="deargod-top" id="medicationNoneTop">
              <div className="pointer"></div>
              <p className="deargodTopTitle" id="med">
                Current Reward
              </p>
            </div>
            <div className = "deargod-rewards" id="rewardsNone">
              {/*<div className="deargod-top" id="rewardsNoneTop">*/}
                {/*<p className="rewardsNoneTopTitle">*/}
                  {/*My Rewards*/}
                {/*</p>*/}
              {/*</div>*/}
              {/*<div className="deargod-bottom" id="rewardsNoneBottom">*/}
                <p id="notif"> You don't have any ongoing rewards.
                  {/*<a href="/profile"><span className="signup-here">Create one here! </span></a>*/}
                </p>
              {/*</div>*/}
              {/*CATH: commented out rewards button*/}
            </div>
          </div>
      );
    }
    else {
      //console.log( this.state.reward_array.img_path);
      // this.getImage(this.state.reward_array.img_path)
      var thumbnail = this.getImage(this.state.reward_array.img_path);
      // //<Image source={{ uri: this.state.reward_array.img_path }} />

      return (
        //  CATH: changed this div from rewardbox to deargod-rewards
        <div className="rewards-container">

          <div className = "deargod-rewards">
            <div className="deargod-top">
              {/*<div className="pointer"></div>*/}
              <p className="deargodTopTitle">
                My Reward
              </p>
            </div>

            {/*add the deargod-top*/}
            <div className = "rewards-home-inner">
              <img src={thumbnail} alt="thumbnail of reward" width="60" height="60"/>
              <div className = "rewards-home-info">
                <p> {this.state.reward_array.desc}</p>
                <p>You have <span>{this.state.reward_array.goalCount - this.state.reward_array.actualCount} </span> day(s) left until
                  you reach your reward for <span> {this.state.reward_array.desc}</span>!
                </p>
              </div>
            </div>
          </div>
        </div>
      );

    }
    //}
  }

  handleCancel() {
    this.setState({ open: false });
  };

  handleClickOpen(){
    this.setState({ open: true });
  };

  handleOK(){
    //this.setState({ open: true });
    // do stuff
    this.setState({ open: false });
  };

  //CATH
  handleScroll() {
    this.setState({scroll: "none"});
  }

  render() {

    if (this.state.toProfile === true){
      return (
        <Redirect to="/profile" />
      );
    }



      if (this.state.completed === null || this.state.username === ""){
        return (
          <div className = "loading-container">
            <BarLoader sizeUnit={"px"} width ={200} height={8} size={500} color={'#01c13b'} />
            <div className="loading-text">Loading... </div>
          </div>);
      }

      else{
        const tileClassName = ({ date, view }) => {
         let getDate = moment(date).format("YYYY/MM/DD");
         // console.log("The days I have are", this.state.completed)
         // console.log("Today, is the day of ", getDate)
         // console.log(this.state.completed.includes(getDate))
         // console.log(this.state.completed[0] )

         if (this.state.completed.includes(getDate)){
           // console.log("here")
           return "highlight-day";
         }
         return null;
       }

      return (<div className = "containerHome">

      <Dialog
        open={this.state.open}
        onClose={this.handleCancel}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Please enter a password."}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            To check off your medication a password is required.
          </DialogContentText>
          <TextField
              autoFocus
              margin="dense"
              label="Medication"
              name="medicine"
              value={this.state.medicine} onChange={this.onChange}
              fullWidth
            />
            <TextField
                autoFocus
                margin="dense"
                label="Description"
                name="medDesc"
                value={this.state.medDesc} onChange={this.onChange}
                fullWidth
              />
              <Checkbox
                checked={this.state.Sunday}
                onChange={this.onCheck}

                name="Sunday"
              />
              <p> Sunday </p>
        </DialogContent>
        <DialogActions>
          <Button onClick={this.handleCancel} color="primary">
            Cancel
          </Button>
          <Button onClick={this.handleOK} color="primary" autoFocus>
            OK
          </Button>
        </DialogActions>
      </Dialog>



        {/*<div className = "top-bar">*/}
          {/*<div className = "toPage">*/}

              {/*<img src={iconprofile} alt="This is the home icon on the home button" onClick = {this.routeChange}></img>*/}

          {/*</div>*/}
        {/*</div>*/}

          <div className="spacer"></div>

          <div className = "user">
            {this.renderUser()}
            <div id ="name">Hello, {this.state.username}!</div>
            {/*CATH: make user name not clickable*/}
            {/*<div id = "name" onClick = {this.routeChange}> Hello, Sam!  </div>*/}
          </div>


        {this.renderMedicine()}

        {this.renderReward()}

            <div className="calendar-container">
              <div className="deargod-top" id="medicationNoneTop">
                {/*<div className="pointer"></div>*/}
                <p className="deargodTopTitle" id="med">
                  My History
                </p>
              </div>
            </div>

              <Calendar
                  tileClassName={tileClassName}
                  locale="en"
              />





          {/*<div className="highlight-box">*/}
            {/*<p> Highlighted days mean you completed all of your medication! </p>*/}
          {/*</div>*/}
          <div className="footer" style={{boxShadow: this.state.scroll}} onScroll={this.handleScroll}>
            <div className="toHome">
              <img src={footerHome}></img>
              <p>home</p>
            </div>
            <div className="toProfile" onClick = {this.routeChange}>
              <img src={footerProfile}></img>
              <p>me</p>
            </div>
          </div>
        </div>

        );
    }
  }
}

export default Home;
