import React, { Component } from 'react';
import { BrowserRouter, Switch, Route, Redirect } from 'react-router-dom'
import logo from './logo.svg';
import './Login.css';

class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      toHome: false,
      toSignup: false,
      username: '',
      password: ''
    }

    this.routeChange = this.routeChange.bind(this);
    this.routeChange2 = this.routeChange2.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.onChange = this.onChange.bind(this);
  }

  routeChange() {
    this.setState({toHome: true});
  }

  routeChange2() {
    this.setState({toSignup: true});
  }

  onChange(e) {
    //console.log(e.target.value)
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  // ZHENGYI COMMENTS:
  // for example, during login to check if username and password are correct:
  // checkLogin( url = server url, data = {}) {
  //   const username = this.state.username;
  //   const password = ...;
  //
  //   //login page tells server to receive this data
  //   fetch() {
  // body = ....
  //   //can send POST request to server
  // }
  handleSubmit(event){
    event.preventDefault()
    console.log("uhm?");

    fetch("http://localhost:5000/login", {
      mode: 'cors',
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        username: this.state.username,
        password: this.state.password,
      })
    })
        .then(res => res.text())
        .then(data => {
          console.log(data);
          //return res;
          // process returned data
        }).catch(err => {
      // handle err
      console.log(err)
    })
  }

  render() {
    if (this.state.toHome === true){
      return (
        <Redirect to="/home" />
      );
    }

    if (this.state.toSignup === true){
      return (
        <Redirect to="/signup" />
      );
    }

    return (
        <div className = "container">
            <div className = "super">
              <img  src={require("./images/transparent-super.png")}
              alt="superhero" width="200" height="200"/>
            </div>


            <form onSubmit = {this.handleSubmit}>
              <div className = "username">
                <p> Account Username </p>
                <input type="text" id="input" name="username" onChange={this.onChange} value={this.state.username}/>
              </div>

              <div className = "password">
                <p> Password </p>
                <input type="password" id = "input" name="password" onChange={this.onChange} value={this.state.password}/>
              </div>
                <p id = "make"> Don't have an account? </p>
                <p id ="make" onClick ={this.routeChange2} > Sign up here!</p>

              <input type="submit" value="Login" id ="submitButton"/>
            </form>
      </div>
    );
  }
}

export default Login;
