import React, { Component } from 'react';
// import { BrowserRouter, Redirect } from 'react-router-dom'
import './Reward.css';

class Reward extends Component {
  constructor(props) {
    super(props);

    this.username = React.createRef();
    this.password = React.createRef();

    this.my_refs = {}

    this.state = {
      desc: '',
      days: "1",
      path: '',
      prevSelect: ''
    }
    this.onChange = this.onChange.bind(this);
    this.focusByID = this.focusByID.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);

    this.server = "http://localhost:5000";
  }

  handleSubmit(){
    console.log(this.state.path)
    fetch(this.server + "/createReward", {
        mode: 'cors',
        credentials: 'include',
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
          'Access-Control-Allow-Credentials': true,
        },
          body: JSON.stringify({
            goalCount: this.state.days,
            desc: this.state.desc,
            img_path: this.state.path
          })
        })
        .then(res => res.json())
        .then(data => {
          this.props.history.push("/home");
        });
  }

  focusByID(id, url){
        // if (this.state.prevSelect != ''){
        //     var curr = document.getElementById(this.state.prevSelect)
        //     // curr.style.border = "1px solid black";
        //     //
        //     // curr.style.transform = "scale(1)";
        //     curr.style.transition = "all 0.2 ease-out";
        // }

        document.getElementById(id).focus()
        // focus.style.border = "2px solid #4AB3F4";
        // focus.style.transition = "all 0.2 ease-in-out";
        // focus.style.transform = "scale(0.8)";

        //document.getElementById(id).focus()
        // let myRef = this.my_refs[id];
        // if(myRef){
        //     console.log('focusing on ', id, myRef);
        //     myRef.focus();
        // }

        //this.setState({prevSelect: id, path: url})
        this.setState({path: url});
    }

  onChange(e) {
       this.setState({
           [e.target.name]: e.target.value
       });
   }

   handleChange (e){
     this.setState({days: e.target.value})
   }

  render() {

    return (
      <div className = "reward-container">
    <div className = "top-bar">
    <div className = "bar">
      <div className = "menu"></div>
      <div className = "menu"></div>
      <div className = "menu"></div>
    </div>
  </div>

    <div className = "goal">
    <p> It's time to set another reward!</p>
      <div className = "inner-goal">

        <form onSubmit={this.handleSubmit}>

        <label>
          Description:
          <input type="text" id ="reward-desc" name ="desc" value={this.state.desc} onChange={this.onChange} />
        </label>

        <label>
          Goal:
          <select value={this.state.days} onChange={this.handleChange}>
            <option value="1">1</option>
            <option value="2">2</option>
          </select>
        </label>

      </form>

      </div>
    </div>

    <div id = "bar"> </div>

    <div className = "reward-choose">
      <p> Choose your reward! </p>
    </div>

    <div className = "reward-library">
      <div className = "reward-box" tabIndex="0" id="icecream"
      onClick={() => this.focusByID('icecream', 'icecream')}>
        <img src={require("./images/icecream.png")} alt="icecweam"
        width="40" height="40"/>
      </div>
      <div className = "reward-box" tabIndex="0" id="bowling"
      onClick={() => this.focusByID('bowling', '')}>
      </div>
      <div className = "reward-box"> </div>
      <div className = "reward-box"> </div>
      <div className = "reward-box"> </div>
      <div className = "reward-box"> </div>
      <div className = "reward-box"> </div>
      <div className = "reward-box"> </div>
      <div className = "reward-box"> </div>
      <div className = "reward-box"> </div>
      <div className = "reward-box"> </div>
      <div className = "reward-box"> </div>
      <div className = "reward-box"> </div>
      <div className = "reward-box"> </div>
      <div className = "reward-box"> </div>

    </div>

    <div className = "footer">
    <div className= "ok-button">
      <input type = "submit" value="OK" id = "OK" onClick = {this.handleSubmit}/>
    </div>
    </div>


  </div>
    );
  }
}

export default Reward;
