import React, { Component } from 'react';
// import { BrowserRouter } from 'react-router-dom'
import './Profile.css';
import Button from '@material-ui/core/Button';
import ChangeMedicine from './ChangeMedicine';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import TextField from '@material-ui/core/TextField';
import Checkbox from '@material-ui/core/Checkbox';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import iconhome from "./images/iconhome2.png";
import footerHome from "./images/web-page-home.png";
import footerProfile from "./images/smile.png";

class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      allMedicine: [],
      addOpen: false,
      sunday: false,
      monday: false,
      tuesday: false,
      wednesday: false,
      thursday: false,
      friday: false,
      saturday: false,
      medicine: '',
      medDesc: '',
      reward_array: [],
      username:'',
      avatar_path: '',
      navColor: ''
    }
    //this.routeChange = this.routeChange.bind(this);
    this.handleCancel = this.handleCancel.bind(this);
    this.handleOKAdd = this.handleOKAdd.bind(this);
    // this.handleOKDelete = this.handleOKDelete.bind(this);
    // this.handleOKEdit = this.handleOKEdit.bind(this);
    this.handleClickOpen = this.handleClickOpen.bind(this);
    this.onCheck = this.onCheck.bind(this);
    this.onChange = this.onChange.bind(this);
    this.goHome = this.goHome.bind(this);
    this.goReward = this.goReward.bind(this);
    this.getImage = this.getImage.bind(this);
    this.getAvatar = this.getAvatar.bind(this);
    this.goAvatar = this.goAvatar.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
    this.rerenderParentCallback = this.rerenderParentCallback.bind(this);

    this.IMAGES = {
      icecream: require('./images/rewards/icecream.png'), // statically analyzed
      baking: require('./images/rewards/baking.png'),
      ball: require('./images/rewards/ball.png'),
      beach: require('./images/rewards/beach.png'),
      bike: require('./images/rewards/bike.png'),
      book: require('./images/rewards/book.png'),
      bowling: require('./images/rewards/bowling.png'),
      card: require('./images/rewards/card.png'),
      carnival: require('./images/rewards/carnival.png'),
      chess: require('./images/rewards/chess.png'), //10
      clothes: require('./images/rewards/clothes.png'),
      ferris: require('./images/rewards/ferris.png'),
      fire: require('./images/rewards/fire.png'),
      game: require('./images/rewards/game.png'),
      gift: require('./images/rewards/gift.png'),
      hamburger: require('./images/rewards/hamburger.png'),
      hotdog: require('./images/rewards/hotdog.png'),
      jewelry: require('./images/rewards/jewelry.png'),
      kart: require('./images/rewards/kart.png'),
      makeup: require('./images/rewards/makeup.png'), //10
      movies: require('./images/rewards/movies.png'),
      paint: require('./images/rewards/paint.png'),
      picnic: require('./images/rewards/picnic.png'),
      pizza: require('./images/rewards/pizza.png'),
      puzzle: require('./images/rewards/puzzle.png'),
      shopping: require('./images/rewards/shopping.png'),
      sneakers: require('./images/rewards/sneakers.png'),
      swimming: require('./images/rewards/swimming.png'),
      teddy: require('./images/rewards/teddy.png'),
      tennis: require('./images/rewards/tennis.png')
    }

    this.HEADS = {
      bunny: require('./images/heads/bunny.png'), // statically analyzed
      cat: require('./images/heads/cat.png'),
      dinosaur: require('./images/heads/dinosaur.png'),
      dog: require('./images/heads/dog.png'),
      frog: require('./images/heads/frog.png'),
      monkey: require('./images/heads/monkey.png'),
      panda: require('./images/heads/panda.png'),
      penguin: require('./images/heads/penguin.png'),
      raccoon: require('./images/heads/raccoon.png'),
      unicorn: require('./images/heads/unicorn.png'), //10
    }

      this.server = "http://localhost:5000";
  }

  getImage(name) { // dynamically invoked
    return this.IMAGES[name];
  }

  getAvatar(name) { // dynamically invoked
    return this.HEADS[name];
  }

  rerenderParentCallback(){
    this.getProfileMedicine();
  }


   /* DYNAMICALLY CHANGING STATE FUNCTIONS  */
  onChange(e) {
       this.setState({
           [e.target.name]: e.target.value
       });
   }

   onCheck(e) {
     this.setState({
         [e.target.name]: e.target.checked
     });
   }


   /* ROUTES */
   goHome(){
        this.props.history.push("/home");
  }

  goAvatar(){
      this.props.history.push("/avatar");
  }

  goReward(){
    this.props.history.push("/reward");
  }


  /* FETCHING INFORMATION WHEN COMPONENT MOUNTS. */
  componentDidMount(){
    //cath
    this.setState({navColor: "#589cfc"});

    fetch(this.server + "/getProfileMedicine", {
      mode: 'cors',
      credentials: 'include',
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Credentials': true,
      }
    })
    .then(res => res.json())
    .then(data => {
      //console.log(data, data[0]) //prints once.
      this.setState({allMedicine: data})
    });

    fetch(this.server + "/getAvatar", {
      mode: 'cors',
      credentials: 'include',
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Credentials': true,
      }
    })
    .then(res => res.json())
    .then(data => {
      console.log("USER DATA", data)
      // console.log("Username?", data.username)

      if (typeof data != 'undefined' && data !== null){
          this.setState({username: data.username, avatar: data.avatar_path});
      }
    });

    fetch(this.server + "/getReward", {
      mode: 'cors',
      credentials: 'include',
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Credentials': true,
      }
    })
    .then(res => res.json())
    .then(data => {
      if (typeof data[0] != 'undefined'){
          this.setState( {reward_array: data[0]} )
      }
    });
  }

  getMedList(){
    return (this.state.allMedicine.map((data, index) => {
      return (<ChangeMedicine key= {index} med={data.medicine} desc={data.description}
              rerenderParentCallback={this.rerenderParentCallback}/>) })
      );
  }

  renderUser(){
    console.log("THIS IS THE AVATAR", this.state.avatar)

    if (this.state.avatar !== "" && typeof this.state.avatar !== 'undefined'){
      var thumbnail = this.getAvatar(this.state.avatar);

      return ( <div className="avatarWrapper"> <img src={thumbnail} alt="thumbnail" width="160" height="160" onClick ={this.goAvatar}/> </div>);
    }
    else{
      return (<div id = "circle" onClick ={this.goAvatar}> </div>);
    }
  }

  handleCancel() {
    this.setState({ addOpen: false });
  };

  handleClickOpen(){
    this.setState({ addOpen: true });
  };

  handleLogout() {
      fetch(this.server + "/logout", {
          mode: 'cors',
          credentials: 'include',
          method: 'GET',
          headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
              'Access-Control-Allow-Credentials': true,
          }
      })
          .then(res => res.text())
          .then(data => {
              if (data === "redirect"){
                  this.props.history.push("/")
              }
          });
  }

  handleOKAdd(){
    fetch(this.server + "/addMedicine", {
        mode: 'cors',
        credentials: 'include',
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
          'Access-Control-Allow-Credentials': true,
        },
          body: JSON.stringify({
            medicine: this.state.medicine,
            medDesc: this.state.medDesc,
            sunday: this.state.sunday,
            monday: this.state.monday,
            tuesday: this.state.tuesday,
            wednesday: this.state.wednesday,
            thursday: this.state.thursday,
            friday: this.state.friday,
            saturday: this.state.saturday
          })
        })
        .then(res => res.text())
        .then(data => {
          // var temp = this.state.allMedicine;
          // temp.push(data);
          this.getProfileMedicine();

          //console.log(temp);
          this.setState({addOpen: false})
        });
    //this.setState({ open: true });
    // do stuff
    //this.setState({ addOpen: false });
  }

  getProfileMedicine(){
    fetch(this.server + "/getProfileMedicine", {
      mode: 'cors',
      credentials: 'include',
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Credentials': true,
      }
    })
    .then(res => res.json())
    .then(data => {
      //console.log(data, data[0]) //prints once.
      console.log(data)
      this.setState({allMedicine: data})
    });
  }

  renderAllMedicine(){
    if (this.state.allMedicine.length === 0){
      return (
        <div className = "deargod-med">
          <p id="notif"> There is no medication currently linked to your account. </p>
          <Button id = "editbutton" variant="outlined" onClick ={this.handleClickOpen}>
            + Add medications
          </Button>
        </div>
      );
    }
    else {
      return (
        <div className = "deargod-med">
            {/*add the deargod-top*/}
            <div className="deargod-top" id="medicationNoneTop">
                <p className="deargodTopTitle" id="med">
                    My Medication
                </p>
            </div>

          {this.getMedList()}

          <Button id = "editbutton" variant="outlined" onClick ={this.handleClickOpen}>
            + Add medications
          </Button>
        </div>
      );
    }
  }

  renderReward(){
    // console.log( this.state.reward_array.desc )
    // console.log( this.state.reward_array.length )
    // console.log(this.state.reward_array.desc == undefined)
    //|| typeof this.state.reward_array.img_path === 'undefined'

    if (this.state.reward_array.length === 0 ){
      //console.log("hi")

      return (

        <div className = "deargod-rewards" id="rewardsNone">
          {/*<div className="deargod-top" id="rewardsNoneTop">*/}
            {/*<p className="rewardsNoneTopTitle">*/}
              {/*My Rewards*/}
            {/*</p>*/}
          {/*</div>*/}
          {/*<div className="deargod-bottom" id="rewardsNoneBottom">*/}
            <p id="notif"> You don't have any ongoing rewards.
              {/*<a href="/profile"><span className="signup-here">Create one here! </span></a>*/}
            </p>
          {/*</div>*/}
          {/*CATH: commented out rewards button*/}
          <Button id = "editbutton" variant="outlined" onClick = {this.goReward}>
            + Create a new reward
          </Button>
        </div>
      );
    }
    else {
      //console.log( this.state.reward_array.img_path);
      // this.getImage(this.state.reward_array.img_path)
      var thumbnail = this.getImage(this.state.reward_array.img_path);
      // //<Image source={{ uri: this.state.reward_array.img_path }} />

      return (
        //  CATH: changed this div from rewardbox to deargod-rewards
        <div className = "deargod-rewards">
          {/*add the deargod-top*/}
          <div className="deargod-top">
            <p className="deargodTopTitle">
              My Current Reward
            </p>
          </div>
          <div className = "rewards-home-inner">
            <img src={thumbnail} alt="icecweam" width="60" height="60"/>
            <div className = "reward-home-info">
              <p> {this.state.reward_array.desc}</p>
              <p> {this.state.reward_array.goalCount - this.state.reward_array.actualCount} day(s) left</p>
            </div>
          </div>
        </div>
      );

    }
    //}
  }

  render() {

    //
    // if (this.state.toReward === true){
    //   return (
    //     <Redirect to="/reward" />
    //   );
    // }
    return (
      <div className = "containerProfile">

      <Dialog
        open={this.state.addOpen}
        onClose={this.handleCancel}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Add medication."}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Please enter the following information.
          </DialogContentText>
          <TextField
              autoFocus
              margin="dense"
              label="Medication"
              name="medicine"
              value={this.state.medicine} onChange={this.onChange}
              fullWidth
            />
            <TextField
                autoFocus
                margin="dense"
                label="Description"
                name="medDesc"
                value={this.state.medDesc} onChange={this.onChange}
                fullWidth
              />
              <FormGroup>
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.sunday}
                        onChange={this.onCheck}
                        name="sunday"
                        />
                      }
                      label="Sunday"
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.monday}
                        onChange={this.onCheck}
                        name="monday"
                        />
                      }
                      label="Monday"
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.tuesday}
                        onChange={this.onCheck}
                        name="tuesday"
                        />
                      }
                      label="Tuesday"
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.wednesday}
                        onChange={this.onCheck}
                        name="wednesday"
                        />
                      }
                      label="Wednesday"
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.thursday}
                        onChange={this.onCheck}
                        name="thursday"
                        />
                      }
                      label="Thursday"
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.friday}
                        onChange={this.onCheck}
                        name="friday"
                        />
                      }
                      label="Friday"
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.saturday}
                        onChange={this.onCheck}
                        name="saturday"
                        />
                      }
                      label="Saturday"
                    />
            </FormGroup>
        </DialogContent>
        <DialogActions>
          <Button onClick={this.handleCancel} color="primary">
            Cancel
          </Button>
          <Button onClick={this.handleOKAdd} color="primary" autoFocus>
            OK
          </Button>
        </DialogActions>
      </Dialog>


        <div className = "user">
        {this.renderUser()}
          <div id = "name"> {this.state.username} </div>
        </div>

        {this.renderAllMedicine()}

        {this.renderReward()}

        <div className="logout">
            <input type="submit" value="Logout" id ="submitButton" onClick ={this.handleLogout}/>
        </div>

        <div className="footer" style={{boxShadow: this.state.scroll}} onScroll={this.handleScroll}>
          <div className="toHome">
            <img src={footerHome} alt="footer-home"></img>
            <p >home</p>
          </div>
          <div className="toProfile" onClick = {this.routeChange}>
            <img src={footerProfile} alt="footer-profile"></img>
            <p style={{color: this.state.navColor}}>me</p>
          </div>
        </div>

      </div>
    );
  }
}

export default Profile;
