import React, { Component } from 'react';
import { Redirect } from 'react-router-dom'
import './Login.css';

class Login extends Component {
  constructor(props) {

    super(props);

    this.state = {
      toHome: false,
      toSignup: false,
      username: '',
      password: ''
    }

    this.routeChange = this.routeChange.bind(this);
    this.routeChange2 = this.routeChange2.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.onChange = this.onChange.bind(this);

    this.server = "http://localhost:5000";
  }

  routeChange() {
    this.setState({toHome: true});
  }

  routeChange2() {
    this.setState({toSignup: true});
  }

  onChange(e) {
    //console.log(e.target.value)
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  // ZHENGYI COMMENTS:
  // for example, during login to check if username and password are correct:
  // checkLogin( url = server url, data = {}) {
  //   const username = this.state.username;
  //   const password = ...;
  //
  //   //login page tells server to receive this data
  //   fetch() {
  // body = ....
  //   //can send POST request to server
  // }
  handleSubmit(event){
    event.preventDefault()
    console.log("uhm?");

    fetch(this.server + "/login", {
      mode: 'cors',
      credentials: 'include',
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Credentials': true,
      },
      body: JSON.stringify({
        username: this.state.username,
        password: this.state.password,
      })
    })
        .then(res => res.text())
        .then(data => {
          console.log(data);
          if (data === "derf success"){
            this.props.history.push("/home");
          }

          //store.set('loggedIn', true);
        }).catch(err => {
      // handle err
      console.log(err)
    })
  }

  render() {
    // if (this.state.toHome === true){
    //   return (
    //     <Redirect to="/home" />
    //   );
    // }

    if (this.state.toSignup === true){
      return (
        <Redirect to="/signup" />
      );
    }

    return (
        <div className = "login-container">

            <div className = "super">
              {/*<img  src={require("./images/transparent-super.png")}
              alt="superhero logo"/>*/}
              <span className="welcome-text">MedHeroes</span>

            </div>


            <form onSubmit = {this.handleSubmit} className="loginForm">
              <div className = "username">
                <p> Username </p>
                <input type="text" id="acct-input" name="username" onChange={this.onChange} value={this.state.username}/>
              </div>

              <div className = "password">
                <p> Password </p>
                <input type="password" id ="pass-input" name="password" onChange={this.onChange} value={this.state.password}/>
              </div>
              <div className="login"> <input type="submit" value="Login" id ="submitButton"/></div>
            </form>

          <div className="signup">
            <p id = "make"> Don't have an account? </p>
            <p id ="make" onClick ={this.routeChange2} className="signup-here" tabIndex="0"> Sign up here!</p>
          </div>
      </div>
    );
  }
}

export default Login;

