import React, { Component } from 'react';
import { Redirect } from 'react-router-dom'
import './App.css';
import Button from '@material-ui/core/Button';
import Medicine from './Medicine';
import Calendar from 'react-calendar';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import TextField from '@material-ui/core/TextField';
import Checkbox from '@material-ui/core/Checkbox';

class Home extends Component {
  constructor(props) {

    super(props);

    this.state = {
      //toProfile: false,
      //form: false,
      medicine: '',
      medicine_array: [],
      reward_array: [],
      image: '',
      open: false,
      medDesc: '',
      Sunday: false
    }

    this.handleCancel = this.handleCancel.bind(this);
    this.handleOK = this.handleOK.bind(this);
    this.handleClickOpen = this.handleClickOpen.bind(this);

    this.renderReward = this.renderReward.bind(this);
    this.renderMedicine = this.renderMedicine.bind(this);
    //this.showForm = this.showForm.bind(this);
    //this.setForm = this.setForm.bind(this);
    this.onChange = this.onChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.getMedList = this.getMedList.bind(this);
    this.goProfile = this.goProfile.bind(this);
    this.getImage = this.getImage.bind(this);
    this.routeChange = this.routeChange.bind(this);

    this.onCheck = this.onCheck.bind(this);
    this.rerenderParentCallback = this.rerenderParentCallback.bind(this);

    this.IMAGES = {
      icecream: require('./images/icecream.png'), // statically analyzed
    }

  }

  rerenderParentCallback(){
    this.setState({reward_array: []});
  }


  getImage(name) { // dynamically invoked
    return this.IMAGES[name];
  }

  onChange(e) {
       this.setState({
           [e.target.name]: e.target.value
       });
   }

   onCheck(e) {
     this.setState({
         [e.target.name]: e.target.checked
     });
   }

  routeChange(event) {
    //this.setState({toProfile: true});
    this.props.history.push("/profile");
    // event.preventDefault()
    // console.log("succesfully here!?");
    //
    // fetch("http://localhost:5000/check", {
    //   mode: 'cors',
    //   credentials: 'include',
    //   method: 'POST',
    //   headers: {
    //     'Accept': 'application/json',
    //     'Content-Type': 'application/json',
    //     'Access-Control-Allow-Credentials': true,
    //   }
    // }).catch(err => {
    //   // handle err
    //   console.log("this bitch did not work")
    // })
  }

  componentDidMount(){
    fetch("http://localhost:5000/getMedicine", {
      mode: 'cors',
      credentials: 'include',
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Credentials': true,
      }
    })
    .then(res => res.json())
    .then(data => {
      var thing = []
      for (var x in data){
        thing.push(data[x].medicine);
      }

      this.setState({medicine_array: thing})
    });

    fetch("http://localhost:5000/getReward", {
      mode: 'cors',
      credentials: 'include',
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Credentials': true,
      }
    })
    .then(res => res.json())
    .then(data => {
      //console.log(data[0])
      //console.log(data[0])
      if (typeof data[0] != 'undefined'){
          this.setState({reward_array: data[0], image: data[0].img_path })
      }

      //console.log(this.state.reward_array)
    });
  }

  handleSubmit(event){
    event.preventDefault()

      fetch("http://localhost:5000/addMedicine", {
          mode: 'cors',
          credentials: 'include',
          method: 'POST',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
            'Access-Control-Allow-Credentials': true,
          },
            body: JSON.stringify({
              medicine: this.state.medicine
            })
          })
          .then(res => res.text())
          .then(data => {
            // console.log("hiiii")
            // this.setState({form: false})

            fetch("http://localhost:5000/getMedicine", {
              mode: 'cors',
              credentials: 'include',
              method: 'GET',
              headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Access-Control-Allow-Credentials': true,
              }
            })
            .then(res => res.json())
            .then(data => {
              var thing = []
              for (var x in data){
                thing.push(data[x].medicine);
              }

              this.setState({medicine_array: thing})
            });
            // if (data.data.code === 200){
            //
            // }
            // console.log(data);
            //return res;
            // process returned data
          }).catch(err => {
            // handle err
            console.log(err)
          })
  }


  // showForm(){
  //   if (this.state.form === true ){
  //     return (
  //       <div className = "display-form">
  //         <form onSubmit={this.handleSubmit}>
  //
  //         <input type="text" id ="medicine-add" name="medicine" onChange={this.onChange} value={this.state.medicine}/>
  //
  //         <input type="submit" value="Add" id ="submitButton-home" onClick={this.removeForm}/>
  //         </form>
  //       </div>
  //     );
  //   }
  // }
  //
  // setForm(){
  //   this.setState({form: true});
  // }

  getMedList(){

    return (this.state.medicine_array.map((data, index) => {
      return (<Medicine key= {index} med={data} rerenderParentCallback={this.rerenderParentCallback}/>) })
    );


    // return (this.state.medicine_array.map( ( med ) => {
    //   console.log(med)
    //     return (<Medicine medicine={med} />) ;
    //   });
    // )
}

//
// <Button id = "editbutton" variant="outlined" onClick ={this.handleClickOpen}>
//   + Add medications
// </Button>

  /* Render the medicines if they exist. AKA, get request here... otherwise,
   * just render the edit button
  */
  renderMedicine(){
    if (this.state.medicine_array.length === 0){
      return (
        <div className = "deargod-med" id="medicationNone">
          {/*<div className="deargod-top" id="medicationNoneTop">*/}
            {/*<p className="deargodTopTitle" id="med">*/}
