import React, { Component } from 'react';
// import { BrowserRouter } from 'react-router-dom'
import './Profile.css';
import Button from '@material-ui/core/Button';
import ChangeMedicine from './ChangeMedicine';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import TextField from '@material-ui/core/TextField';
import Checkbox from '@material-ui/core/Checkbox';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';

class Profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      allMedicine: [],
      addOpen: false,
      sunday: false,
      monday: false,
      tuesday: false,
      wednesday: false,
      thursday: false,
      friday: false,
      saturday: false,
      medicine: '',
      medDesc: ''
    }
    //this.routeChange = this.routeChange.bind(this);
    this.handleCancel = this.handleCancel.bind(this);
    this.handleOKAdd = this.handleOKAdd.bind(this);
    // this.handleOKDelete = this.handleOKDelete.bind(this);
    // this.handleOKEdit = this.handleOKEdit.bind(this);
    this.handleClickOpen = this.handleClickOpen.bind(this);
    this.onCheck = this.onCheck.bind(this);
    this.onChange = this.onChange.bind(this);
  }

  onChange(e) {
       this.setState({
           [e.target.name]: e.target.value
       });
   }

  onCheck(e) {
    this.setState({
        [e.target.name]: e.target.checked
    });
  }

  componentDidMount(){
    fetch("http://localhost:5000/getMedicine", {
      mode: 'cors',
      credentials: 'include',
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Access-Control-Allow-Credentials': true,
      }
    })
    .then(res => res.json())
    .then(data => {
      //console.log(data, data[0]) //prints once.
      this.setState({allMedicine: data})
    });
  }

  getMedList(){
    return (this.state.allMedicine.map((data, index) => {
      return (<ChangeMedicine key= {index} med={data.medicine} desc={data.description}/>) })
      );
  }

  handleCancel() {
    this.setState({ addOpen: false });
  };

  handleClickOpen(){
    this.setState({ addOpen: true });
  };

  handleOKAdd(){
    fetch("http://localhost:5000/addMedicine", {
        mode: 'cors',
        credentials: 'include',
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
          'Access-Control-Allow-Credentials': true,
        },
          body: JSON.stringify({
            medicine: this.state.medicine,
            medDesc: this.state.medDesc,
            sunday: this.state.sunday,
            monday: this.state.monday,
            tuesday: this.state.tuesday,
            wednesday: this.state.wednesday,
            thursday: this.state.thursday,
            friday: this.state.friday,
            saturday: this.state.saturday
          })
        })
        .then(res => res.text())
        .then(data => {
          // var temp = this.state.allMedicine;
          // temp.push(data);
          fetch("http://localhost:5000/getMedicine", {
            mode: 'cors',
            credentials: 'include',
            method: 'GET',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
              'Access-Control-Allow-Credentials': true,
            }
          })
          .then(res => res.json())
          .then(data => {
            //console.log(data, data[0]) //prints once.
            console.log(data)
            this.setState({allMedicine: data})
          });

          //console.log(temp);
          this.setState({addOpen: false})
        });
    //this.setState({ open: true });
    // do stuff
    //this.setState({ addOpen: false });
  }

  renderAllMedicine(){
    if (this.state.allMedicine.length === 0){
      return (
        <div className = "deargod" id="medicationNone">
          <p id="notif"> There is no medication currently linked to your account. </p>
          <Button id = "editbutton" variant="outlined" onClick ={this.handleClickOpen}>
            + Add medications
          </Button>
        </div>
      );
    }
    else {
      return (
        <div className = "deargod">

          {this.getMedList()}

          <Button id = "editbutton" variant="outlined" onClick ={this.handleClickOpen}>
            + Add medications
          </Button>
        </div>
      );
    }
  }

  render() {

    //
    // if (this.state.toReward === true){
    //   return (
    //     <Redirect to="/reward" />
    //   );
    // }
    return (
      <div className = "containerProfile">

      <Dialog
        open={this.state.addOpen}
        onClose={this.handleCancel}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Add medication."}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Please enter the following information.
          </DialogContentText>
          <TextField
              autoFocus
              margin="dense"
              label="Medication"
              name="medicine"
              value={this.state.medicine} onChange={this.onChange}
              fullWidth
            />
            <TextField
                autoFocus
                margin="dense"
                label="Description"
                name="medDesc"
                value={this.state.medDesc} onChange={this.onChange}
                fullWidth
              />
              <FormGroup>
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.sunday}
                        onChange={this.onCheck}
                        name="sunday"
                        />
                      }
                      label="Sunday"
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.monday}
                        onChange={this.onCheck}
                        name="monday"
                        />
                      }
                      label="Monday"
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.tuesday}
                        onChange={this.onCheck}
                        name="tuesday"
                        />
                      }
                      label="Tuesday"
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.wednesday}
                        onChange={this.onCheck}
                        name="wednesday"
                        />
                      }
                      label="Wednesday"
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.thursday}
                        onChange={this.onCheck}
                        name="thursday"
                        />
                      }
                      label="Thursday"
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.friday}
                        onChange={this.onCheck}
                        name="friday"
                        />
                      }
                      label="Friday"
                    />
                    <FormControlLabel
                      control={
                        <Checkbox
                        checked={this.state.saturday}
                        onChange={this.onCheck}
                        name="saturday"
                        />
                      }
                      label="Saturday"
                    />
            </FormGroup>
        </DialogContent>
        <DialogActions>
          <Button onClick={this.handleCancel} color="primary">
            Cancel
          </Button>
          <Button onClick={this.handleOKAdd} color="primary" autoFocus>
            OK
          </Button>
        </DialogActions>
      </Dialog>


      <div className = "top-bar">
          <a href="/home">
              <div className = "toPage">
              </div>
          </a>
      </div>

        <div className = "user">
          <div id = "circle"></div>
          <div id = "name"> Sam </div>
        </div>

        {this.renderAllMedicine()}


        <div className = "reward" >
          <div className= "inner">
          <img
          src={require("./images/reward.png")} alt="icecweam"
          width="70" height="70"/>
            <p> Set a reward </p>
          </div>
        </div>

      </div>
    );
  }
}

export default Profile;
